#ifndef SUParameterAssert_h
#define SUParameterAssert_h

// Work around a warning generated by some versions of Xcode that we still support,
// when using NSParameterAssert
#define SUParameterAssert(condition) \
NSAssert((condition), @"Invalid parameter not satisfying: %@", @(#condition))

#endif /* SUParameterAssert_h */
